mapper.update:
UPDATE Member 
SET member_point = member_point - #{batingAmount}
WHERE member_no = #{memberNo}

UPDATE Member
SET member_point = member_point + #{batingAmount} * 1.5
WHERE member_no = #{memberNo}

UPDATE Member
SET member_point = member_point + #{batingAmount} * 3
WHERE member_no = #{memberNo}

UPDATE Member
SET member_point = member_point + #{batingAmount}
WHERE member_no = #{memberNo}
